// Helper function to get emote name from ID
export function getEmoteName(emoteId: string): string | undefined {
  return emoteMapping[emoteId];
}

// Reverse mapping to get ID from name
export const emoteNameToId: Record<string, string> = Object.entries(emoteMapping).reduce(
  (acc, [id, name]) => ({ ...acc, [name]: id }),
  {}
);

// Helper function to get emote ID from name
export function getEmoteId(emoteName: string): string | undefined {
  return emoteNameToId[emoteName];
}
